select T.ID, T.PROJECT_ID, T.USER_ID, T.COURSE_ID, T.TRAIN_ID, T.TEACHER_ID, T.INSEPARABLE, T.ACCURACY, T.LIVELY, T.DEEP_GOING, T.CLEAR, T.INSIDE_DOPESTER, T.PROPERLY, T.BODY_LANGUAGE, T.APPROPRIATE, T.ACTIVE, T.CREATOR, T.CREATE_TIME, T.UPDATEDBY, T.UPDATE_TIME, T.IS_DELETED, T.VERSION, T.TOTAL_NUM from IPR_TEACHING_EVALUA T where T.IS_DELETED='N'
AND T.TRAIN_ID = #{paramMap.registerTrainId,jdbcType=VARCHAR}
ORDER BY T.CREATE_TIME,T.ID DESC

select T.ID, T.PROJECT_ID, T.USER_ID, T.COURSE_ID, T.TRAIN_ID, T.TEACHER_ID, T.INSEPARABLE, T.ACCURACY, T.LIVELY, T.DEEP_GOING, T.CLEAR, T.INSIDE_DOPESTER, T.PROPERLY, T.BODY_LANGUAGE, T.APPROPRIATE, T.ACTIVE, T.CREATOR, T.CREATE_TIME, T.UPDATEDBY, T.UPDATE_TIME, T.IS_DELETED, T.VERSION, t.TOTAL_NUM from IPR_TEACHING_EVALUA T where T.IS_DELETED='N'
AND T.TRAIN_ID = #{paramMap.registerTrainId,jdbcType=VARCHAR}
ORDER BY T.CREATE_TIME,T.ID DESC
